import * as React from 'react';
import classNames from 'classnames';
import {SIZE, ALIGNMENT} from './ContentBoxConstants';

type SizeType =
  | 'xxsmall'
  | 'xsmall'
  | 'small'
  | 'normal'
  | 'large'
  | 'xlarge'
  | 'xxlarge';

type AligmentType = 'left' | 'center' | 'right';

export type ContentBoxActionsPropsType = {
  children: React.Node,
  className?: ?string,
  spacedTop?: ?SizeType,
  spacedBottom?: ?SizeType,
  align?: AligmentType,
  ...
