import * as React from 'react';
import classNames from 'classnames';

export type SpinnerSizeType = 'small' | 'xsmall' | 'xxsmall';

export type SpinnerColorType =
  | 'black'
  | 'white'
  | 'gray-900'
  | 'gray-700'
  | 'peach-700'
  | 'mustard-700'
  | 'blue-700';

